// DisciplineList.js
import React, { useEffect } from "react";
import { observer } from "mobx-react-lite";
import disciplineStore from "../store/DisciplineStore";

const DisciplineList = observer(() => {
  useEffect(() => {
    disciplineStore.fetchDisciplines();
  }, []);

  if (disciplineStore.isLoading) {
    return <div>Loading...</div>;
  }

  if (disciplineStore.error) {
    return <div>Error: {disciplineStore.error}</div>;
  }

  return (
    <div>
      {disciplineStore.disciplines.length > 0 ? (
        <ul>
          {disciplineStore.disciplines.map((discipline) => (
            <li key={discipline.id}>
              <img src={discipline.image} alt={discipline.name} />
              {discipline.name}
            </li>
          ))}
        </ul>
      ) : (
        <div>No disciplines available</div>
      )}
    </div>
  );
});

export default DisciplineList;
