import { Col } from 'react-bootstrap';
import React, { useEffect, useState } from 'react';  
import { useParams } from 'react-router-dom';
import { fetchDesciplinesData } from '../api/countryAPI';
const DisciplineDetails = () => {

    const [disciplineData, setDisciplineData] = useState(null); // Данные о дисциплине
    const { disciplineName, countryName } = useParams(); // Получение параметров из URL

    useEffect(() => {
        console.log("Fetching data for discipline:", disciplineName, "and country:", countryName);
        if (disciplineName && countryName) {
            fetchDesciplinesData(disciplineName, countryName)  // Используем fetchDesciplinesData
                .then(data => setDisciplineData(data))  // Данные о дисциплине и стране
                .catch(error => console.error("Error fetching disciplines data:", error));
        }
    }, [disciplineName, countryName]);

    if (!disciplineData) {
        return <div>Loading...</div>;
    }

    return (
        
        <Col>
            <Col className="p-0">
                <h1 className="h1">{disciplineName}</h1>
                <img className="discipline" src={`/images/disciplines/${disciplineName}.svg`} alt={disciplineName} />
                <h2 className="h1">{countryName}</h2>
            </Col>
            <Col>
                <table className="table">
                    <thead>
                        <tr className="tableRow">
                            <th className="tableHeader">GOLD</th>
                            <th className="tableHeader">SILVER</th>
                            <th className="tableHeader">BRONZE</th>
                            <th className="tableHeader">TOTAL</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr className="tableRow">
                            <td className="tableCell">
                                {disciplineData.gold}
                            </td>
                            <td className="tableCell">
                                {disciplineData.silver}
                            </td>
                            <td className="tableCell">
                                {disciplineData.bronze}
                            </td>
                            <td className="tableCell">
                                {disciplineData.gold + disciplineData.silver + disciplineData.bronze}
                            </td>
                        </tr>
                    </tbody>
                </table>
            </Col>
        </Col>
    );
};

export default DisciplineDetails;
