import React from 'react';
import { Container, Row } from "react-bootstrap";
import { observer } from "mobx-react-lite";

const Shop = observer(() => {
    const containerStyle = {
        position: 'relative',
        backgroundImage: 'url(/images/bg.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '0',  // Убираем отступы
        height: '100vh' // Высота контейнера по экрану
    };

    const imgStyle = {
        position: 'absolute',
        display: 'block',
    };

    const logoStyle = {
        ...imgStyle,
    };

    const frameStyle = {
        ...imgStyle,
    };

    const icoCountriesStyle = {
        ...imgStyle,
        bottom: '20px',
        left: '50px',
        width: '50px',  // Размер изображения
    };

    const icoDisciplinesStyle = {
        ...imgStyle,
        bottom: '20px',
        right: '50px',
        width: '50px',  // Размер изображения
    };

    return (
        <Container style={containerStyle}>
            <Row className="mt-2">
                <img src='/images/logo-white.png' alt='paris' style={logoStyle} />
            </Row>
            <Row className="mt-2">
                <img src='/images/frame.png' alt='paris' style={frameStyle} />
            </Row>
            <Row className="mt-20">
                <img src='/images/ico-countries.svg' alt='paris' style={icoCountriesStyle} />
            </Row>
            <Row className="mt-2">
                <img src='/images/ico-disciplines.svg' alt='paris' style={icoDisciplinesStyle} />
            </Row>
        </Container>
    );
});

export default Shop;
