import axios from 'axios';

export const fetchCountryMedals = (countryName) => {
    return axios.get('http://localhost:3000/countries')
        .then(response => {
            const country = response.data.find(c => c.name === countryName);
            if (!country) {
                throw new Error(`Country with name ${countryName} not found`);
            }
            return country;
        })
        .catch(error => {
            console.error('Error fetching country medals:', error);
            throw error;
        });
};

export const fetchD = (countryName) => {
    return axios.get('http://localhost:3000/countries')
        .then(response => {
            const country = response.data.find(d => d.name === countryName);
            if (!country) {
                throw new Error(`Country with name ${countryName} not found`);
            }
            return country;
        })
        .catch(error => {
            console.error('Error fetching country medals:', error);
            throw error;
        });
};


export const fetchDiscipline = (countryName, disciplineName) => {
    return axios.get('http://localhost:3000/countries')
        .then(response => {
            // Находим страну по имени
            const country = response.data.find(c => c.name === countryName);
            if (!country) {
                throw new Error(`Country with name ${countryName} not found`);
            }

            // Находим дисциплину по имени
            const discipline = country.disciplines.find(d => d.name === disciplineName);
            if (!discipline) {
                throw new Error(`Discipline with name ${disciplineName} not found in country ${countryName}`);
            }

            // Возвращаем дисциплину
            return discipline;
        })
        .catch(error => {
            console.error('Error fetching discipline:', error);
            throw error;
        });
};

// Функция для получения данных по дисциплине и стране
export const fetchDisciplines = (disciplineName, countryName) => {
    return axios.get('http://localhost:3000/disciplineData') // Предположим, что данные находятся здесь
        .then(response => {
            // Фильтруем по дисциплине и стране
            const disciplineData = response.data.find(discipline => discipline.name === disciplineName);
            if (!disciplineData) {
                throw new Error(`Discipline with name ${disciplineName} not found`);
            }
            
            const countryData = disciplineData.countries.find(country => country.name === countryName);
            if (!countryData) {
                throw new Error(`Country with name ${countryName} not found in discipline ${disciplineName}`);
            }
            return countryData;  // Возвращаем данные только для выбранной дисциплины и страны
        })
        .catch(error => {
            console.error('Error fetching discipline data:', error);
            throw error;
        });
};
