import React, { useState, useEffect } from "react";
import { Col } from "react-bootstrap";
import { useHistory } from 'react-router-dom';
import { DISCIPLINES_ROUTE } from "../utils/consts";

export function DisciplineList() {
    const [disciplines, setDisciplines] = useState([]);
    const [loading, setLoading] = useState(true);

    const history = useHistory();

    const handleDisciplineClick = (name) => {
        history.push(`${DISCIPLINES_ROUTE}/${name}`);
    };

    useEffect(() => {
        // Загрузка данных
        fetch("http://localhost:3000/countries")
            .then((response) => response.json())
            .then((data) => {
                // Собираем все дисциплины из стран
                const allDisciplines = data.countries.flatMap(country => country.disciplines);
                setDisciplines(allDisciplines);
                setLoading(false);
            })
            .catch((error) => {
                console.error("Ошибка загрузки:", error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Загрузка...</div>;
    }

    if (disciplines.length === 0) {
        return <div>Нет дисциплин для отображения</div>;
    }

    return (
        <Col className="disciplines">
            {disciplines.map((discipline, index) => (
                <div className="i" onClick={() => handleDisciplineClick(discipline.name)} key={index}>
                    <img className="flag" src={`/${discipline.image}`} alt={discipline.name} />
                    {discipline.name}
                </div>
            ))}
        </Col>
    );
}
