import React, { useState, useEffect } from "react";
import { Col } from "react-bootstrap";
import { useHistory } from 'react-router-dom';


export function DisciplineList() {
  const [disciplines, setDisciplines] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const history = useHistory();

    const handleCountryClick = (name) => {
        history.push(`${DISCIPLINES_ROUTE}/${name}`);
    };

    // Загрузка данных
    fetch("http://localhost:3000/countries")
      .then((response) => response.json())
      .then((data) => {
        setDisciplines(data); // Устанавливаем данные
        setLoading(false);
      })
      .catch((error) => {
        console.error("Ошибка загрузки:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (disciplines.length === 0) {
    return <div>Нет дисциплин для отображения</div>;
  }

  return (
    <Col className="countries">
      {disciplines.map((discipline) => (
        <div className="i" onClick={() => handleCountryClick(discipline.name)} key={discipline.name} >
        <img className="flag" src={`/images/disciplines/${discipline.name}.svg`} alt='Athletic' />
        {discipline.name}</div>
      ))}
    </Col>
  );
}
