import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';
import { COUNTRIES_ROUTE } from '../utils/consts';
import { containerStyle, NavBar } from '../components/NavBar';

const Countries = () => {
    const [countriesData, setCountriesData] = useState([]);
    const history = useHistory();

    useEffect(() => {
        axios.get('http://localhost:3000/countries')
            .then(response => {
                setCountriesData(response.data);
            })
            .catch(error => {
                console.error('Error fetching countries data:', error);
            });
    }, []);

    const handleCountryClick = (name) => {
        history.push(`${COUNTRIES_ROUTE}/${name}`);
    };


    return (
        <Container className="containerStyle">
            <NavBar/>
            <h1 className="h1">Countries</h1>
            <Row>
            </Row>
            <Col className="countries">
                {countriesData.map((country) => (
                    <div key={country.name} className="i" onClick={() => handleCountryClick(country.name)}>
                        <img className="flag" src={`/images/flags/${country.name.replace(' ', '')}.png`} alt={country.name} />
                        {country.name}
                    </div>
                ))}
            </Col>
        </Container>
    );
};

export default Countries;
