import React, { useEffect, useState } from 'react';  
import { useHistory, useParams } from 'react-router-dom';
import { fetchDisciplineData } from '../api/countryAPI';  // Импортируем обновленную функцию
import { Container, Col, Row } from 'react-bootstrap'; 
import { COUNTRIES_ROUTE } from '../utils/consts';

const DisciplineCountry = () => {
    const [countryData, setCountryData] = useState(null);
    const { name, cou } = useParams();  // Получаем дисциплину и страну из URL

    const history = useHistory();

    // Фетчим данные по дисциплине и стране
    useEffect(() => {
        if (name && cou) {
            fetchDisciplineData(name, cou)  // Используем fetchDisciplineData
                .then(data => setCountryData(data))
                .catch(error => console.error(error));
        }
    }, [name, cou]);

    if (!countryData) {
        return <div>Loading...</div>;
    }

    const containerStyle = {
        position: 'relative',
        backgroundImage: 'url(/images/bg.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '0px',
        height: '100vh',
        width: '100vw',
        color: "white"
    };

    const imgStyle = {
        display: 'block',
    };

    const prev = {
        ...imgStyle,
        top: '15px',
        left: '10px',
        margin: "30px"
    };

    const logo = {
        ...imgStyle,
        top: '15px',
        left: '150px',
        margin: "15px",
        marginLeft: "71px",
        height: "57px",
    };

    const flag = {
        marginLeft: "135px",
    };

    const h1 = {
        color: "white",
        textAlign: "center",
    };

    const table = {
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: '20px',
        width: '100%',
    };

    const tableChild1 = {
        padding: "0px 5px 20px 5px",
        textAlign: 'center',
        border: "1px solid white",
        borderTop: "0px",
        margin: "0px",
    };

    const totalMedalsStyle = {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
    };

    const g = {
        padding: "20px 20.485px 0px 20.485px",
        textAlign: 'center',
        border: "1px solid white",
        borderBottom: "0px",
    };

    // Отображаем данные на странице
    return (
        <Container style={containerStyle}>
            <Row>
                <img style={prev} src='/images/ico-prev.svg' alt='paris' onClick={() => history.push(COUNTRIES_ROUTE + `/${countryData.country.name}`)} />
                <img style={logo} src='/images/logo-sm-white.png' alt='paris' />
            </Row>
            <Col className='p-0'>
                <h1 style={h1}>{countryData.country.name}</h1>
                <img style={flag} src={`/images/flags/${countryData.country.name.replace(' ', '')}.png`} alt={countryData.country.name} />
            </Col>
            <Col style={totalMedalsStyle}></Col>
            <table>
                <tr style={table}>
                    <th><h5 style={tableChild1}>DISCIPLINE</h5></th>
                    <th><h5 style={tableChild1}>MEDALS</h5></th>
                </tr>
                <tr key={"key"} style={table}>
                    <td><h5 style={g}>{countryData.discipline.name}</h5></td>
                    <td><h5 style={g}>{countryData.discipline.gold + countryData.discipline.silver + countryData.discipline.bronze}</h5></td>
                </tr>
            </table>
        </Container>
    );
};

export default DisciplineCountry;
