import React, { useState } from 'react';
import { Switch, Route, Redirect, useLocation } from 'react-router-dom';
import { TransitionGroup, CSSTransition } from 'react-transition-group';
import { publicRoutes } from "../routes";
import { SHOP_ROUTE } from "../utils/consts";
import { observer } from "mobx-react-lite";
import '../styles/transitions.css';

const AppRouter = observer(() => {
    const location = useLocation();
    const [isBackward, setIsBackward] = useState(false);

    // Массив путей для анимации из комментария
    const customAnimationPaths = ['/countries', '/disciplines',''];

    window.onpopstate = () => {
        setIsBackward(true);
    };

    const isCustomAnimation = customAnimationPaths.includes(location.pathname);

    return (
        <TransitionGroup>
            <CSSTransition
                key={location.key}
                timeout={750}
                classNames={{
                    enter: `page-enter ${isBackward ? 'backward' : 'forward'}${isCustomAnimation ? '-custom' : ''}`,
                    enterActive: `page-enter-active ${isBackward ? 'backward' : 'forward'}${isCustomAnimation ? '-custom' : ''}`,
                    exit: `page-exit ${isBackward ? 'backward' : 'forward'}${isCustomAnimation ? '-custom' : ''}`,
                    exitActive: `page-exit-active ${isBackward ? 'backward' : 'forward'}${isCustomAnimation ? '-custom' : ''}`
                }}
                onExited={() => setIsBackward(false)}
                unmountOnExit
            >
                <Switch location={location}>
                    {publicRoutes.map(({ path, Component }) =>
                        <Route key={path} path={path} component={Component} exact />
                    )}
                    <Redirect to={SHOP_ROUTE} />
                </Switch>
            </CSSTransition>
        </TransitionGroup>
    );
});

export default AppRouter;
