import React from 'react';
import { Col, Container, Row } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import {useHistory} from "react-router-dom"
import { COUNTRIES_ROUTE, SHOP_ROUTE } from '../utils/consts';

const Shop = observer(() => {
    const history = useHistory()
    const containerStyle = {
        position: 'relative',
        backgroundImage: 'url(/images/bg.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '0px',  
        height: '100vh' ,
        width: '100vw'
    };
    
    const imgStyle = {
        position: 'absolute',
        display: 'block',
    };

    const i = {
        color: "white",
        border: '1px solid white',
        width: '300px',
        height:'50px'
    };

    const prev = {
        ...imgStyle,
        top: '15px',
        left: '10px',
    };

    const logo = {
        ...imgStyle,
        top: '15px',
        left: '150px',
    };

    const flag = {
        width:'40px',
        margin_left: '10px'
    };

    const countries = {
        ...imgStyle,
        top: '150px',
        left: '65px',
        padding: '0px', 
    };

    return (
        <Container style={containerStyle}>
            <Row >
                <img style={prev} src='/images/ico-prev.svg' alt='paris' onClick={() => history.push(SHOP_ROUTE)}></img>
                <img style={logo} src='/images/logo-sm-white.png' alt='paris' />
            </Row>
            <Row >

            </Row>
            <Col style={countries}>
                
                <div style={i} onClick={() => history.push(COUNTRIES_ROUTE + '/')}>
                <img style={flag} src='\images\flags\USA.png' alt='paris' />
                USA
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\China.png' alt='paris' />
                China
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\Japan.png' alt='paris' />
                Japan
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\Germany.png' alt='paris' />
                Germany
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\Australia.png' alt='paris' />
                Australia
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\Russia.png' alt='paris' />
                Russia
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\UnitedKingdom.png' alt='paris' />
                United Kingdom
                </div>
                <div style={i} href=''>
                <img style={flag} src='\images\flags\France.png' alt='paris' />
                France
                </div>
            </Col>
        </Container>
    )
});

export default Shop;
