import React from 'react';
import { Col, Container, Row } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import {useHistory} from "react-router-dom"
import { COUNTRIES_ROUTE, SHOP_ROUTE } from '../utils/consts';

const Shop = observer(() => {
    const history = useHistory()
    const containerStyle = {
        position: 'relative',
        backgroundImage: 'url(/images/bg.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '0px',  
        height: '100vh' ,
        width: '100vw'
    };
    
    const imgStyle = {
        position: 'absolute',
        display: 'block',
    };

    const i1 = {
        color: "white",
        border: '1px solid white',
        width: '300px'
    };

    const i2 = {
        color: "white",
        border: '1px solid white',
        width: '300px'
    };

    const prev = {
        ...imgStyle,
        top: '15px',
        left: '10px',
    };

    const logo = {
        ...imgStyle,
        top: '15px',
        left: '150px',
    };

    const flag = {
        ...imgStyle,
        width:'40px'
    };

    const countries = {
        ...imgStyle,
        top: '150px',
        left: '65px',
        padding: '0px', 
    };

    return (
        <Container style={containerStyle}>
            <Row >
                <img style={prev} src='/images/ico-prev.svg' alt='paris' onClick={() => history.push(SHOP_ROUTE)}></img>
                <img style={logo} src='/images/logo-sm-white.png' alt='paris' />
            </Row>
            <Row >
                
            </Row>
            <Col style={countries}>
                <div style={i1} onClick={() => history.push(COUNTRIES_ROUTE + '/')}>
                <img style={flag} src='\images\flags\USA.png' alt='paris' />
                USA
                </div>
                <div style={i2} href=''>
                <img src='\images\flags\China.png' alt='paris' />
                China
                </div>
            </Col>
        </Container>
    );
});

export default Shop;
