import React, { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { fetchCountryMedals } from '../api/countryAPI';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import { COUNTRIES_ROUTE } from '../utils/consts';

const CountryPage = () => {
    const [country, setCountry] = useState(null);
    const { name } = useParams();
    const history = useHistory();

    const i = {
        color: "white",
        border: '3px solid white',
        width: '300px',
        height:'60px',
        margin: '10px',
        borderRadius: '20px',
        backgroundColor:"rgba(255, 255, 255, 0.29)",
        cursor: "pointer",
    };


    useEffect(() => {
        fetchCountryMedals(name)
            .then(data => setCountry(data))
            .catch(error => console.error(error));
    }, [name]);

    const handleMedalClick = (medalType) => {
        history.push(`/medals/${medalType}`);
    };

    if (!country) {
        return <div>Loading...</div>;
    }

    return (
        <Container className="mt-3">
            <Button onClick={() => history.push(COUNTRIES_ROUTE)}>Назад</Button>
            <h1>{country.name}</h1>
            <img  src={`/images/flags/${country.name.replace(' ', '')}.png`} alt={country.name} />
            <Col>
                <Col md={4}>
                    <Card className="d-flex flex-column align-items-center justify-content-around" style={i}>
                        <h3>Золото: {country.medals.gold}</h3>
                        <Button variant={"outline-gold"} onClick={() => handleMedalClick('gold')}>Перейти</Button>
                    </Card>
                </Col>
                <Col md={4}>
                    <Card className="d-flex flex-column align-items-center justify-content-around" style={i}>
                        <h3>Серебро: {country.medals.silver}</h3>
                        <Button variant={"outline-silver"} onClick={() => handleMedalClick('silver')}>Перейти</Button>
                    </Card>
                </Col>
                <Col md={4}>
                    <Card className="d-flex flex-column align-items-center justify-content-around" style={i}>
                        <h3>Бронза: {country.medals.bronze}</h3>
                        <Button variant={"outline-bronze"} onClick={() => handleMedalClick('bronze')}>Перейти</Button>
                    </Card>
                </Col>
            </Col>
            <h2>Общее количество медалей: {country.medals.gold + country.medals.silver + country.medals.bronze}</h2>
        </Container>
    );
};

export default CountryPage;
